mapper.select:

select count(id)  from  member 



	 appid = #{appid} and openid = #{openid} 



		and eid = #{eid}

mapper.insert:
insert into member(eid,appid,name,mobile,totalMoney,balance,createTime,openid,unionid,status,avatarUrl,recommendThreeId,recommendTwoId,recommendOneId) SELECT #{eid} as eid ,#{appid} as appid,#{name} as name,#{mobile} as mobile,#{totalMoney} as totalMoney,#{balance} as balance, unix_timestamp(now())*1000 as createTime,#{openid} as openid,#{unionid} as unionid,0 as status,#{avatarUrl} as avatarUrl,
a.recommendTwoId as recommendThreeId,a.recommendOneId as recommendTwoId,#{recommendOneId} as recommendOneId from member a where a.openid=#{recommendOneId}
'' as recommendThreeId,'' as recommendTwoId,'' as recommendOneId from dual

update.foreach:

			update member



				 totalMoney = #{income.money} + totalMoney,




           		openid=#{income.openid} and appid = #{income.appid}

mapper.update:

			update member



				 balance = balance - #{balance} 




           		appid = #{appid} and openid=#{openid} and status='0' and balance >= #{balance}

mapper.select:

select * from member



	appid = #{appid} and recommendOneId = #{openid}



		and DATE_FORMAT(createTime,'%Y-%m-%d') = DATE_FORMAT(#{createTime},'%Y-%m-%d')

select b.openid,b.eid, b.name,b.createTime,sum(b.money) as balance
from (select t.name,t.eid,a.createTime,t.openid,t.money FROM
incomeDetails t ,
member a where t.openid = a.openid and t.eid = #{eid}
and t.Level=1 and
t.openid in


			#{member.openid}


) b group by b.openid, b.name,b.createTime

select b.openid, b.name,b.eid,b.createTime,sum(b.money) as balance
from (select t.name,t.eid,a.createTime,t.openid,t.money FROM
incomeDetails t ,
member a where t.openid = a.openid and t.eid = #{eid}
and t.Level=2 and
t.openid in


			#{member.openid}


) b group by b.openid, b.name,b.createTime
