mapper.select:
select
count(1)
from inOutDepot


	and inOutDepotTypeName = #{inoutdepottypename}


	and No = #{no}


	and inOutDepotTypeNo = #{inoutdepottypeno}

select * from inOutDepot



		and No like #{'%'+no+'%'}


		and inoutDepotTypeId = #{inoutDepotTypeId}


		and createDate = #{createDate}


		and auditingDate = #{auditingDate}


		and accountId = #{accountId}


		and Money = #{money}


		and type = #{type}


		and Status = #{status}


		and sid = #{sid}


		and eid = #{eid}

mapper.delete:
delete from
inOutDepot
where Id = #{id} and sid = #{sid} and eid = #{eid}
