mapper.select:
select * from inOutDepot

where 1=1


	and inOutDepotTypeName like #{query_name}



	and No like #{query_no}



	and inOutDepotTypeNo like #{query_typeno}


	and inOutDepotType = #{inoutdepottype,jdbcType=CHAR}



	and Remark like #{query_remark}


	and CreateDate = #{createdate,jdbcType=TIMESTAMP}


	and AuditingDate = #{auditingdate,jdbcType=TIMESTAMP}



	and AccountName like #{query_accountname}


	and AccountId = #{accountid,jdbcType=VARCHAR}


	and Money = #{money,jdbcType=DOUBLE}


	and Status = #{status,jdbcType=CHAR}


	and sid = #{sid,jdbcType=INTEGER}


	and eid = #{eid,jdbcType=INTEGER}
